                            <form >
                                <textarea [(ngModel)]="questionName" [value]="qu.question" rows="7"
                                          cols="45"
                                          name="comment">
                                </textarea>
                                <br>
                            </form>

                            </div>
                                            <!-- Submit button -->
                            <button type="submit" class="btn btn-primary">Edit</button>
                                            &nbsp;
                            <button type="button" class="btn btn-warning " data-dismiss="modal">Close</button>
                        </form>
                   </div>
                </div>
            </div>
        </div>
        <!------------------------------------End Modal------------------------------------->

        <!---------------------------------Delete Question--------------------------------->

        <div class="modal fade" [id]=" 'del' + qu.questionId" tabindex="-1" role="dialog" aria-labelledby="exampleModalCenterTitle" aria-hidden="true">
            <div class="modal-dialog modal-dialog-centered" role="document">
              <div class="modal-content">

                <div class="modal-header">
                  <h5 class="modal-title" id="exampleModalLongTitle"><b>Are you sure ?</b> <span> &nbsp;<i class="fas fas fa-chalkboard" style="color: #0b29ab;"></i></span></h5>
                  <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                  </button>
                </div>

                <div class="modal-footer">
                  <button type="button" class="btn btn-warning" data-dismiss="modal" (click)="relo
                  ">Close</button>
                  <button type="submit" class="btn btn-danger" (click)="delete(qu.questionId)">Delete</button>
                </div>

              </div>
            </div>
          </div>

        <!----------------------------------Delete Modal----------------------------------->
        </div>

    </div>
    <br>

</div>
