                <div class="modal-body p-4">
                    <form  #groupForm="ngForm" (ngSubmit)="editGroup(groupId)">
                        <!-- Name input -->
                        <div class="form-outline mb-4">
                          <label class="form-label" >Group Name</label>
                          <input [(ngModel)]="class.groupName" name="groupName" type="text"  class="form-control" />
                        </div>
                        <!-- Desc input -->
                        <div class="form-outline mb-4">
                          <label class="form-label" >Group Description</label>
                          <input [(ngModel)]="class.groupDesc" name="groupDesc" type="textarea"  class="form-control" />
                      </div>

                      <!-- Group grade -->
                      <label class="form-label" for="name2">Group Grade</label>
                      <select [(ngModel)]="class.groupGrad" class="form-control" name="groupGrad" aria-placeholder="Choose your grade">
                        <option value="undefined" selected disabled>Select Grade</option>
                        <option *ngFor="let grad of grades" [ngValue]="grad.gradId">{{grad.gradName}}</option>
                    </select>
                      <br>
                    <!-- Group Subject-->
                    <select [(ngModel)]="class.groupSub" class="form-control" name="groupSub" aria-placeholder="Select your grade">
                      <option value="undefined" selected disabled>Select Subject</option>
                      <option *ngFor="let sub of subjects" [ngValue]="sub.subId">{{sub.subName}}</option>
                  </select>
                  <label class="form-label" for="name2">Group Subject</label>

                        <!-- Limit input -->
                        <div class="form-outline mb-4">
                          <label class="form-label" for="email2">Group Limit</label>
                            <input type="number" [(ngModel)]="class.groupLimit" name="groupLimit" class="form-control" />
                        </div>



                        <!-- Submit button -->
                        <button type="submit" class="btn btn-primary btn-block">Add</button>
                    </form>
                </div>
            </div>
        </div>
    </div>
    <!--------------------------------------End Modal------------------------------------>

    <!-------------------------------Delete Class-------------------------------------------->

    <div class="modal fade" id="deleteClass" tabindex="-1" role="dialog" aria-labelledby="exampleModalCenterTitle" aria-hidden="true">
        <div class="modal-dialog modal-dialog-centered" role="document">
          <div class="modal-content">

            <div class="modal-header">
              <h5 class="modal-title" id="exampleModalLongTitle"><b>Are you sure ?</b> <span> &nbsp;<i class="fas fas fa-chalkboard" style="color: #0b29ab;"></i></span></h5>
              <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                <span aria-hidden="true">&times;</span>
              </button>
            </div>

            <div class="modal-footer">
              <button type="button" class="btn btn-warning" data-dismiss="modal" (click)="reload()">Close</button>
              <button type="submit" class="btn btn-danger" (click)="deleteGroup(groupId)">Delete</button>
            </div>

          </div>
        </div>
      </div>
</div>
</div>

